import React, {useState} from 'react';

interface IForm {
    firstName: string,
    lastName: string
}

const ChildForms: React.FC = () => {
    const [state,setState] = useState<IForm>({
        firstName:'',
        lastName:''
    });

    const formHandleChanged = (event: React.ChangeEvent<HTMLInputElement>) => {
        setState(({
            ...state,
            [event.target.name] : event.target.value
        }))
    };

    return(
        <div>
            <input value={state.firstName} name="firstName" onChange={formHandleChanged.bind(this)} type="text"  />
            <input value={state.lastName} name="lastName" onChange={formHandleChanged.bind(this)} type="text"  />
        </div>
    )
};

export default ChildForms;
